import React, { useState, useEffect } from "react";

const Time = () => {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const intervalId = setInterval(() => {
      setTime(new Date());
    }, 1000); // 1000 milliseconds (1 second)

    return () => {
      clearInterval(intervalId);
    };
  }, []);

  const sec = time.getSeconds().toString().padStart(2, "0");
  const min = time.getMinutes().toString().padStart(2, "0");
  const hr24 = time.getHours().toString().padStart(2, "0");
  const hr12 = (hr24 % 12) || 12; // Convert to 12-hour format
  const ampm = hr24 < 12 ? "AM" : "PM";

  return (
    <div className="clock">
      <div className="lag">
        <p>Lagos</p>
        <h2>
          <span id="hr">{hr12}</span>:{min}:{sec} {ampm}
        </h2>
      </div>
      <br /> <hr />
      <div className="nyc">
        <p>New York</p>
        <h2>
          <span id="hr">{((hr12 - 5 + 12) % 12).toString().padStart(2, "0")}</span>:{min}:{sec} {ampm}
        </h2>
      </div>
    </div>
  );
};

export default Time;
