import React, { useState, useEffect } from "react";
import timeZone from "./timeZone";

const Time = () => {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const intervalId = setInterval(() => {
      setTime(new Date());
    }, 1000); // 1000 milliseconds (1 second)

    return () => {
      clearInterval(intervalId);
    };
  }, []);

  const localTime = time.toLocaleTimeString(undefined, {
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit',
  });

  const getFormattedTime = (timeZone) => {
    return time.toLocaleTimeString(undefined, {
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit',
      timeZone,
    });
  };

  /* const lagosTime = getFormattedTime('Africa/Lagos');
  const newYorkTime = getFormattedTime('America/New_York');
  const chinaTime = getFormattedTime('Asia/Shanghai');
  const parisTime = getFormattedTime('Europe/Paris');
  const pakistanTime = getFormattedTime('Asia/Karachi'); */

  return (
    <div className="clock">
      
      <br />
      <hr />
      <div className="lag">
        <p>Lagos</p>
        <h2>{timeZone}[0]</h2>
      </div>
      <br />
      <hr />
      <div className="nyc">
        <p>New York</p>
        <h2>{newYorkTime}</h2>
      </div>
      <br />
      <hr />
      <div className="china">
        <p>China</p>
        <h2>{chinaTime}</h2>
      </div>
      <br />
      <hr />
      <div className="paris">
        <p>Paris</p>
        <h2>{parisTime}</h2>
      </div>
      <div className="nZealand">
        <p>Pakistan</p>
        <h2>{pakistanTime}</h2>
      </div>
    </div>
  );
};

export default Time;
